class ToggleLikes{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let l=parseInt($(e).attr("data-likes"));t.deleted?l-=1:l+=1,$(e).attr("data-likes",l),$(e).text(`${l} Likes`)})).fail((function(t){console.log("error while completing the request")}))}))}}